class ReplicationError {
  constructor(
      type: Option.Type, maturity: number, strike: number, s0: number,
      sigma: number, r: number) {
    this._maturity = maturity;
    this._payoff = new PlainVanillaPayoff(type, strike);
    this._s0 = s0;
    this._sigma = sigma;
    this._r = r;

    // value of the option
    const rDiscount: DiscountFactor = Math.exp(-this._r * this._maturity);
    const qDiscount: DiscountFactor = 1.0;
    const forward: number = this._s0 * qDiscount / rDiscount;
    const stdDev: number = Math.sqrt(this._sigma * this._sigma * this._maturity);
    const payoff: StrikedTypePayoff = new PlainVanillaPayoff(type, strike);
    const black: BlackCalculator =
        new BlackCalculator().init1(payoff, forward, stdDev, rDiscount);

    print(`Option value: ${black.value()}`);

    // store option's vega, since Derman and Kamal's formula needs it
    this._vega = black.vega(this._maturity);
  }

  // the actual replication error computation
  compute(nTimeSteps: number, nSamples: number): void {
    if (nTimeSteps <= 0) {
      throw new Error('the number of steps must be > 0');
    }

    // hedging interval
    // Time tau = maturity_ / nTimeSteps;

    /* Black-Scholes framework: the underlying stock price evolves
       lognormally with a fixed known volatility that stays constant
       throughout time.
    */
    const calendar: Calendar = new TARGET();
    const today: Date = DateExt.UTC();
    const dayCount: DayCounter = new Actual365Fixed();
    const stateVariable: Handle<Quote> = new Handle(new SimpleQuote(this._s0));
    const riskFreeRate: Handle<FlatForward> =
        new Handle(new FlatForward().ffInit2(today, this._r, dayCount));
    const dividendYield: Handle<FlatForward> =
        new Handle(new FlatForward().ffInit2(today, 0.0, dayCount));
    const volatility: Handle<BlackVolTermStructure> =
        new Handle(new BlackConstantVol().bcvInit1(
            today, calendar, this._sigma, dayCount));
    const diffusion: StochasticProcess1D = new BlackScholesMertonProcess(
        stateVariable, dividendYield, riskFreeRate, volatility);

    // Black Scholes equation rules the path generator:
    // at each step the log of the stock
    // will have drift and sigma^2 variance
    const rsg: InverseCumulativeRsg =
        new PseudoRandom().make_sequence_generator(nTimeSteps, 0);

    const brownianBridge = false;

    const myPathGenerator: PathGenerator = new PathGenerator().init1(
        diffusion, this._maturity, nTimeSteps, rsg, brownianBridge);

    // The replication strategy's Profit&Loss is computed for each path
    // of the stock. The path pricer knows how to price a path using its
    // value() method
    const myPathPricer: PathPricer<Path> = new ReplicationPathPricer(
        this._payoff.optionType(), this._payoff.strike(), this._r,
        this._maturity, this._sigma);

    // a statistics accumulator for the path-dependant Profit&Loss values
    const statisticsAccumulator: RiskStatistics = new RiskStatistics();

    // The Monte Carlo model generates paths using myPathGenerator
    // each path is priced using myPathPricer
    // prices will be accumulated into statisticsAccumulator
    // tslint:disable-next-line:variable-name
    const MCSimulation: MonteCarloModel =
        new MonteCarloModel(MC.SingleVariate, new PseudoRandom())
            .mcmInit(
                myPathGenerator, myPathPricer, statisticsAccumulator, false);

    // the model simulates nSamples paths
    MCSimulation.addSamples(nSamples);

    // the sampleAccumulator method
    // gives access to all the methods of statisticsAccumulator
    // tslint:disable-next-line:variable-name
    const PLMean: number = MCSimulation.sampleAccumulator().mean();
    // tslint:disable-next-line:variable-name
    const PLStDev: number = MCSimulation.sampleAccumulator().standardDeviation();
    // tslint:disable-next-line:variable-name
    const PLSkew: number = MCSimulation.sampleAccumulator().skewness();
    // tslint:disable-next-line:variable-name
    const PLKurt: number = MCSimulation.sampleAccumulator().kurtosis();

    // Derman and Kamal's formula
    const theorStD: number =
        Math.sqrt(M_PI / 4 / nTimeSteps) * this._vega * this._sigma;

    report.push({
      samples: nSamples,
      trades: nTimeSteps,
      PLMean: PLMean.toFixed(3),
      PLStDev: PLStDev.toFixed(2),
      DermanKamal: theorStD.toFixed(2),
      PLSkew: PLSkew.toFixed(2),
      PLKurt: PLKurt.toFixed(2)
    })
  }

  private _maturity: number;
  _payoff: PlainVanillaPayoff;
  private _s0: number;
  private _sigma: number;
  private _r: number;
  private _vega: number;
}

class ReplicationPathPricer extends PathPricer<Path> {
  constructor(
      type: Option.Type, strike: number, r: number, maturity: number,
      sigma: number) {
    super();
    this._type = type;
    this._strike = strike;
    this._r = r;
    this._maturity = maturity;
    this._sigma = sigma;

    if (this._strike <= 0) {
      throw new Error('strike must be positive');
    }
    if (this._r < 0) {
      throw new Error('risk free rate (r) must be positive or zero');
    }
    if (this._maturity <= 0) {
      throw new Error('maturity must be positive');
    }
    if (this._sigma < 0) {
      throw new Error('volatility (sigma) must be positive or zero');
    }
  }

  /**
   * The actual computation of the Profit&Loss for each single path.
   *
   * In each scenario N rehedging trades spaced evenly in time over
   * the life of the option are carried out, using the Black-Scholes
   * hedge ratio.
   */
  f(path: Path): number {
    const n: number = path.length() - 1;
    if (n <= 0) {
      throw new Error('the path cannot be empty');
    }

    // discrete hedging interval
    const dt: number = this._maturity / n;

    // For simplicity, we assume the stock pays no dividends.
    const stockDividendYield: Rate = 0.0;

    // let's start
    let t: number = 0;

    // stock value at t=0
    let stock: number = path.front;

    // money account at t=0
    // tslint:disable-next-line:variable-name
    let money_account: number = 0.0;

    /************************/
    /*** the initial deal ***/
    /************************/
    // option fair price (Black-Scholes) at t=0
    let rDiscount: DiscountFactor = Math.exp(-this._r * this._maturity);
    let qDiscount: DiscountFactor =
        Math.exp(-stockDividendYield * this._maturity);
    let forward: number = stock * qDiscount / rDiscount;
    let stdDev: number = Math.sqrt(this._sigma * this._sigma * this._maturity);
    const payoff: StrikedTypePayoff =
        new PlainVanillaPayoff(this._type, this._strike);
    const black: BlackCalculator =
        new BlackCalculator().init1(payoff, forward, stdDev, rDiscount);
    // sell the option, cash in its premium
    money_account += black.value();
    // compute delta
    let delta: number = black.delta(stock);
    // delta-hedge the option buying stock
    let stockAmount: number = delta;
    money_account -= stockAmount * stock;

    /**********************************/
    /*** hedging during option life ***/
    /**********************************/
    for (let step = 0; step < n - 1; step++) {
      // time flows
      t += dt;

      // accruing on the money account
      money_account *= Math.exp(this._r * dt);

      // stock growth:
      stock = path.at(step + 1);

      // recalculate option value at the current stock value,
      // and the current time to maturity
      rDiscount = Math.exp(-this._r * (this._maturity - t));
      qDiscount = Math.exp(-stockDividendYield * (this._maturity - t));
      forward = stock * qDiscount / rDiscount;
      stdDev = Math.sqrt(this._sigma * this._sigma * (this._maturity - t));
      const black: BlackCalculator =
          new BlackCalculator().init1(payoff, forward, stdDev, rDiscount);

      // recalculate delta
      delta = black.delta(stock);

      // re-hedging
      money_account -= (delta - stockAmount) * stock;
      stockAmount = delta;
    }

    /*************************/
    /*** option expiration ***/
    /*************************/
    // last accrual on my money account
    money_account *= Math.exp(this._r * dt);
    // last stock growth
    stock = path.at(n);

    // the hedger delivers the option payoff to the option holder
    const optionPayoff: number =
        new PlainVanillaPayoff(this._type, this._strike).f(stock);
    money_account -= optionPayoff;

    // and unwinds the hedge selling his stock position
    money_account += stockAmount * stock;

    // final Profit&Loss
    return money_account;
  }

  private _type: Option.Type;
  private _strike: number;
  private _r: number;
  private _maturity: number;
  private _sigma: number;
}

try {
  let seconds = 0;
  const startTime: number = Date.now();
  const maturity: number = 1.0 / 12.0;  // 1 month
  const strike: number = 100;
  const underlying: number = 100;
  const volatility: number = 0.20;  // 20%
  const riskFreeRate: number = 0.05;      // 5%
  const rp: ReplicationError = new ReplicationError(
      Option.Type.Call, maturity, strike, underlying, volatility, riskFreeRate);

  const scenarios: number = 50000;
  let hedgesNum: number;

  hedgesNum = 21;
  rp.compute(hedgesNum, scenarios);

  hedgesNum = 84;
  rp.compute(hedgesNum, scenarios);

  const endTime: number = Date.now();

  seconds = (endTime - startTime) / 1000;

  table(report)

  print(`Run completed in ${seconds} seconds`)

} catch (e) {
  print(e);
}
